tinymce.PluginManager.add('custom_modules', function(editor, url) {
  // Add a button that opens a window
  editor.addButton('custom_modules', {
   // text: 'My button',
    icon: false,
	image: '/cms/internal/gfx/custom_modules.gif',
    onclick: function() {
      // Open window
	  
	   editor.windowManager.open({
        title: 'TinyMCE site',
        url: '/',
        width: 800,
        height: 600,
        buttons: [{
          text: 'Close',
          onclick: 'close'
        }]
      });
	  
	  /*
      editor.windowManager.open({
        title: 'Example plugin',
        body: [
          {type: 'textbox', name: 'title', label: 'Title'}
        ],
        onsubmit: function(e) {
          // Insert content when the window form is submitted
          editor.insertContent('Title: ' + e.data.title);
        }
      });*/
    }
  });

  // Adds a menu item to the tools menu
  editor.addMenuItem('custom_modules', {
    text: 'Example plugin',
    context: 'tools',
    onclick: function() {
      // Open window with a specific url
      editor.windowManager.open({
        title: 'TinyMCE site',
        url: '/',
        width: 800,
        height: 600,
        buttons: [{
          text: 'Close',
          onclick: 'close'
        }]
      });
    }
  });

  return {
    getMetadata: function () {
      return  {
        name: "Example plugin",
        url: "http://exampleplugindocsurl.com"
      };
    }
  };
});